import './App.css';
import {sampleText} from './sampleText';
import { Component } from 'react';

class App extends Component {
  state = { 
    text: sampleText
   }
  render() {
    return (
      <div className='container'>
        <div className='row'>
          <div className='col-sm-6'>
            <textarea value={this.state.text} 
            className='form-control' rows='35'>
            </textarea>
          </div>
          <div className='col-sm-6'>
        <div>{sampleText}</div>
        </div>
      </div>
    </div>
  );
}
}
export default App;
